import React, { useState, useEffect } from 'react';
import Category from './DTO/Category.tsx';
import FavoriteRecipe from './DTO/FavoriteRecipe.tsx';
import LoginHistory from './DTO/LoginHistory.tsx';
import Recipe from './DTO/Recipe.tsx';
import axios from 'axios';
import Login from './components/LoginForm.js'; 

const Recipes = () => {
    const [recipes, setRecipes] = useState<Recipe[]>([]);
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    useEffect(() => {
        const fetchRecipes = async () => {
            try {
                const token = localStorage.getItem('token');
                if (token) {
                    const response = await axios.get('http://localhost:4000/recipes', {
                        headers: { Authorization: `Bearer ${token}` }
                    });
                    setRecipes(response.data.recipes);
                    setIsLoggedIn(true);
                }
            } catch (err) {
                console.error(err);
            }
        };

        fetchRecipes();
    }, []);

    const handleLoginSuccess = () => {
        setIsLoggedIn(true);
        fetchRecipes();
    };

    const fetchRecipes = async () => {
        try {
            const token = localStorage.getItem('token');
            if (token) {
                const response = await axios.get('http://localhost:4000/recipes', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setRecipes(response.data.recipes);
            }
        } catch (err) {
            console.error(err);
        }
    };

    return (
        <div>
           {!isLoggedIn ? (
                <Login onLoginSuccess={handleLoginSuccess} />
            ) : (
                <div>
                    <h1>Recipes</h1>
                    <ul>
                        {recipes.map((recipe) => (
                            <li key={recipe.recipe_id}>
                                <h2>{recipe.recipe_name}</h2>
                                <p>{recipe.ingredients}</p>
                                <p>{recipe.instructions}</p>
                                <p>Preparation Time: {recipe.prepare_time} minutes</p>
                                <p>Calories: {recipe.calories}</p>
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default Recipes;
